mapper.select:
SELECT id, name, level, class_id, subclass_id, inventory_type, quality, icon, crafting_tier, is_stackable, expansion_id, created_at, updated_at FROM item WHERE 1 = 1
AND class_id = #{query.classId}
AND subclass_id = #{query.subclassId}
AND EXISTS ( SELECT 1 FROM jsonb_each_text(name) WHERE value ILIKE CONCAT('%', #{query.name}, '%') )
